import barbarian from './assets/static/barbarian.png';
import cleric from './assets/static/cleric.png';
import druid from './assets/static/druid.png';
import fighter from './assets/static/fighter.png';
import monk from './assets/static/monk.png';
import paladin from './assets/static/paladin.png';
import ranger from './assets/static/ranger.png';
import rogue from './assets/static/rogue.png';
import sorcer from './assets/static/sorcer.png';
import wizard from './assets/static/wizard.png';

export const fetchJson = (name, link, setFunctions) => {
  const { setOpen, setErrorResponse, setJson } = setFunctions;
  const storage = sessionStorage.getItem(name);
  if (storage) {
    setJson(JSON.parse(sessionStorage.getItem(name)));
  } else {
    fetch(`https://api.jsonbin.io/b/${link}`, {
      headers: {
        'secret-key': '$2b$10$rMiLIzrwZN3L0coOy9YBuO.EbU9bdYqwH79HBShMOATzq1YNKzSvC',
      },
    })
      .catch((error) => {
        console.log(`Hubo un problema con la petición Fetch:${error.message}`);
        if (!document.body.classList.contains('overflow-hidden')) {
          document.body.classList.add('overflow-hidden');
        }
        setOpen(true);
        setErrorResponse(error.message);
      })
      .then((response) => {
        return response.json();
      })
      .then((myJson) => {
        if ((!myJson.success && myJson.success !== undefined) || myJson.lenght <= 0) {
          if (!document.body.classList.contains('overflow-hidden')) {
            document.body.classList.add('overflow-hidden');
          }
          setOpen(true);
          setErrorResponse(myJson.message);
        } else {
          setJson(myJson);
          sessionStorage.setItem(name, JSON.stringify(myJson));
        }
      });
  }
};

export const fetchJson2 = (url) => {
  return fetch(`https://api.jsonbin.io/b/${url}`, {
    headers: {
      'secret-key': '$2b$10$eXuE8LaH5rObba2/EXN62ubykgkFAuJpQhfJKmcfwUp8WpirYSXhW',
    },
  })
    .then((response) => {
      return response.json();
    });
};

export const getImgClassHero = (className) => {
  let classNameSrc;
  switch (className.toLowerCase()) {
    case 'barbaro':
      classNameSrc = barbarian;
      break;
    case 'clerigo':
      classNameSrc = cleric;
      break;
    case 'druida':
      classNameSrc = druid;
      break;
    case 'luchador':
      classNameSrc = fighter;
      break;
    case 'monje':
      classNameSrc = monk;
      break;
    case 'paladin':
      classNameSrc = paladin;
      break;
    case 'guardabosques':
      classNameSrc = ranger;
      break;
    case 'rebelde':
      classNameSrc = rogue;
      break;
    case 'hechicero':
      classNameSrc = sorcer;
      break;
    case 'brujo':
      classNameSrc = wizard;
      break;
    default:
      break;
  }
  return classNameSrc;
};
