import LabelInput from './LabelInput'
import FilterButton from './FilterButton'
import ButtonClassSearch from './ButtonClassSearch';

const Buscador = ({ dataHeroes, setData, setClean }) => {

  const handleFilterStars = (cantStars) => {
    for (let i = 1; i <= cantStars; i++) {
      if (document.querySelector(`.star-${i}`).classList.contains('filter-gray')) {
        document.querySelector(`.star-${i}`).classList.remove('filter-gray');
      }
    }
    for (let i = cantStars + 1; i <= 5; i++) {
      if (!document.querySelector(`.star-${i}`).classList.contains('filter-gray')) {
        document.querySelector(`.star-${i}`).classList.add('filter-gray');
      }
    }
    setData({
      ...dataHeroes,
      stars: `${cantStars !== 0 ? cantStars : ''}`,
    });
  };

  const handleElement = (element) => {
    let nameElement;
    for (let i = 1; i <= 5; i++) {
      if (i === element) {
        if (document.querySelector(`.element-${element}`).classList.contains('filter-gray')) {
          document.querySelector(`.element-${element}`).classList.remove('filter-gray');
        }
      } else {
        if (!document.querySelector(`.element-${i}`).classList.contains('filter-gray')) {
          document.querySelector(`.element-${i}`).classList.add('filter-gray');
        }
      }
    }
    switch (element) {
      case 1:
        nameElement = 'Rojo Fuego';
        break;
      case 2:
        nameElement = 'Azul Hielo';
        break;
      case 3:
        nameElement = 'Verde Naturaleza';
        break;
      case 4:
        nameElement = 'Violeta Oscuro';
        break;
      case 5:
        nameElement = 'Amarillo Sagrado';
        break;
    }
    setData({
      ...dataHeroes,
      element: `${nameElement}`,
    });
  };

  const handleClassSearch = (e, name) => {
    if (!e.currentTarget.classList.contains('buttonClassSearch-active')) {
      document.querySelectorAll('.buttonClassSearch').forEach((button) => {
        if (button.classList.contains('buttonClassSearch-active')) {
          button.classList.remove('buttonClassSearch-active');
        }
      });
      e.currentTarget.classList.add('buttonClassSearch-active');
      setData({
        ...dataHeroes,
        class: name,
      });
    }
  };

  const handleClean = () => {
    Array.from(document.querySelectorAll('.buttonClassSearch')).some((button) => {
      if (button.classList.contains('buttonClassSearch-active')) {
        button.classList.remove('buttonClassSearch-active');
      }
      return button.classList.contains('buttonClassSearch-active');
    });
    setClean(true);
    handleFilterStars(0);
    handleElement(0);
  };

  return (
    <div className='sm:grid sm:grid-cols-3 sm:gap-2 lg:grid-cols-1'>
      <h2 className='flex items-center justify-center text-xl font-bold md:text-lg md:block'>Buscador:</h2>
      <LabelInput
        value={dataHeroes.query}
        handleChange={(e) => {
          setData({
            ...dataHeroes,
            query: e.target.value,
          });
        }}
        placeholder='Ameonna'
      >
        Nombre:
      </LabelInput>
      <div className='w-full mt-1 space-y-4'>
        <div className='grid grid-cols-5 gap-1'>
          <FilterButton handleOnClick={handleFilterStars} number={1} img='../../assets/static/star.png' identity='star'>star</FilterButton>
          <FilterButton handleOnClick={handleFilterStars} number={2} img='../../assets/static/star.png' identity='star'>star</FilterButton>
          <FilterButton handleOnClick={handleFilterStars} number={3} img='../../assets/static/star.png' identity='star'>star</FilterButton>
          <FilterButton handleOnClick={handleFilterStars} number={4} img='../../assets/static/star.png' identity='star'>star</FilterButton>
          <FilterButton handleOnClick={handleFilterStars} number={5} img='../../assets/static/star.png' identity='star'>star</FilterButton>
        </div>
        <div className='grid grid-cols-5 gap-1'>
          <FilterButton handleOnClick={handleElement} number={1} img='../../assets/static/fuego.png' identity='element'>Fuego</FilterButton>
          <FilterButton handleOnClick={handleElement} number={2} img='../../assets/static/hielo.png' identity='element'>Hielo</FilterButton>
          <FilterButton handleOnClick={handleElement} number={3} img='../../assets/static/naturaleza.png' identity='element'>Naturaleza</FilterButton>
          <FilterButton handleOnClick={handleElement} number={4} img='../../assets/static/oscuro.png' identity='element'>Oscuro</FilterButton>
          <FilterButton handleOnClick={handleElement} number={5} img='../../assets/static/sagrado.png' identity='element'>Sagrado</FilterButton>
        </div>
      </div>
      <div className='grid grid-cols-5 gap-2 p-2 mt-4 border border-pink-400 rounded lg:p-1 xl:p-2'>
        <ButtonClassSearch image='../../assets/static/barbarian' name='Barbaro' handleClick={handleClassSearch} />
        <ButtonClassSearch image='../../assets/static/cleric' name='Clerigo' handleClick={handleClassSearch} />
        <ButtonClassSearch image='../../assets/static/druid' name='Druida' handleClick={handleClassSearch} />
        <ButtonClassSearch image='../../assets/static/fighter' name='Luchador' handleClick={handleClassSearch} />
        <ButtonClassSearch image='../../assets/static/monk' name='Monje' handleClick={handleClassSearch} />
        <ButtonClassSearch image='../../assets/static/paladin' name='Paladin' handleClick={handleClassSearch} />
        <ButtonClassSearch image='../../assets/static/ranger' name='Guardabosques' handleClick={handleClassSearch} />
        <ButtonClassSearch image='../../assets/static/rogue' name='Rebelde' handleClick={handleClassSearch} />
        <ButtonClassSearch image='../../assets/static/sorcer' name='Hechicero' handleClick={handleClassSearch} />
        <ButtonClassSearch image='../../assets/static/wizard' name='Brujo' handleClick={handleClassSearch} />
      </div>
      <div className='w-full mt-2 space-y-2'>
        <div>
          <LabelInput
            placeholder='Sakura'
            value={dataHeroes.family}
            handleChange={(e) => {
              setData({
                ...dataHeroes,
                family: e.target.value,
              });
            }}
          >
            Familia:
          </LabelInput>
        </div>
        <div>
          <LabelInput
            placeholder='Segunda Temporada'
            value={dataHeroes.event}
            handleChange={(e) => {
              setData({
                ...dataHeroes,
                event: e.target.value,
              });
            }}
          >
            Evento:
          </LabelInput>
        </div>
      </div>
      <div className='pt-4 text-right sm:flex sm:justify-center sm:items-center'>
        <button className='px-2 py-1 font-semibold text-pink-700 bg-transparent border border-pink-500 rounded hover:bg-pink-500 hover:text-white hover:border-transparent' type='button' onClick={handleClean}>Limpiar</button>
      </div>
    </div>
  )
}

export default Buscador
