import ReactDOM from 'react-dom';
import { useEffect } from 'react';
import { animated } from 'react-spring';
import { useFade, useSlide } from '../animations';

const Modal = ({ children, data }) => {

  if (!data.open) {
    return null;
  }

  const { slide, setSlide } = useSlide(0, -500);
  const { fade, setFade } = useFade();

  const closeModal = () => {
    setSlide({ transform: 'translate(0px, -500px)' });
    setFade({
      to: { opacity: 0 },
      onRest: () => {
        if (document.body.classList.contains('overflow-hidden')) {
          document.body.classList.remove('overflow-hidden');
        }
        data.setOpen(false);
      },
    });
  };

  const handleClickBackground = (e) => {
    if (e.target === document.querySelector('.Modal__bg')) {
      closeModal();
    }
  };

  useEffect(() => {
    if (document.querySelector('.Modal')) {
      if (!document.body.classList.contains('overflow-hidden')) {
        document.body.classList.add('overflow-hidden');
      }
      document.querySelector('.Modal__bg').addEventListener('click', (e) => {
        handleClickBackground(e);
      });
    }
    return () => {
      if (document.body.classList.contains('overflow-hidden')) {
        document.body.classList.remove('overflow-hidden');
      }
    };
  }, []);

  /*return ReactDOM.createPortal(
    <animated.div className='z-30 overflow-x-hidden overflow-y-auto Modal' style={fade}>
      <div className='flex items-center justify-center w-full min-h-screen py-6 Modal__bg'>
        <animated.div className='rounded-lg Modal__container' style={slide}>
          <button className='absolute z-10 px-2 py-1 text-xl font-bold leading-none text-white bg-red-800 border border-black rounded-lg shadow Modal__close-button text-border-black hover:bg-red-600' type='button' onClick={() => { closeModal(); }}>
              x
          </button>
          {children}
        </animated.div>
      </div>
      <style jsx>
        {`
          .Modal {
            position: fixed;
            top: 0;
            left: 0;
            bottom: 0;
            right: 0;
            background-color: rgba(0, 0, 0, 0.5);
          }

          .Modal__container {
            position: relative;
            background-color: rgb(255, 255, 255);
          }

          .Modal__close-button {
            top: -0.5rem;
            right: -0.5rem;
          }
        `}
      </style>
    </animated.div>
    , document.getElementById('modal'));*/
  return ReactDOM.createPortal(
    <div className='z-30 Modal animated fadeIn faster'>
      <div className='overflow-auto rounded-lg Modal__container animated slideInDown faster'>
        <button className='z-10 m-2 Modal__close-button' type='button' onClick={() => { onClose(); }}>
          <img className='object-contain w-4 h-4' src='/assets/static/cross.svg' alt='close' />
        </button>
        {children}
      </div>
      <style jsx>
        {`
            .Modal {
              position: fixed;
              top: 0;
              left: 0;
              bottom: 0;
              right: 0;
              background-color: rgba(0, 0, 0, 0.5);
              display: flex;
              flex-direction: column;
              align-items: center;
              justify-content: center;
            }
  
            .Modal__container {
              position: relative;
              background-color: rgb(255, 255, 255);
            }
  
            .Modal__close-button {
              position: absolute;
              top: 0;
              right: 0;
              color: black;
            }
          `}
      </style>
    </div>,
    document.getElementById('modal'),
  );
};

export default Modal;
